// Library implementation of new_deck.cpp#include "new_deck.h"// create_deck takes a structure "deck" and places 52 // cards in it with values varying from 0 to 12 and suit // 0 to 3.deck create_deck(deck &mydeck){	int i;		// Creates unshuffled deck	for (i = 0; i < 52; i++)	{		mydeck.cards[i].value = (i % 13);		mydeck.cards[i].suit  = (i / 13);	}	return mydeck;}// shuffle_deck is given an unshuffled deck of cards and // makes that deck shuffled using an array of random numbers.deck shuffle_deck(deck &mydeck){	int i, j, temp;	int shuffle[52];	card temps;		// An array named "shuffle[]" is allotted 52 blocks and a 	// random number is placed in each one.		for (i = 0; i < 52; i++)	{		shuffle[i] = rand();	}		i = 1;		// The array of 52 random numbers created above is now 	// sorted and whatever is done to "shuffle[]" is done to 	// the inputted, unshuffled deck of cards.		while (i > 0)	{					i = 0;		for (j = 0; j < 51; j++)		{							if ((shuffle[j]) < (shuffle[(j + 1)]))			{					temp = shuffle[j];				shuffle[j] = shuffle[(j + 1)];				shuffle[(j + 1)] = temp;								temps = mydeck.cards[j];				mydeck.cards[j] = mydeck.cards[(j + 1)];				mydeck.cards[(j + 1)] = temps;				i++;			}		}	}	return mydeck;}