// Library implementation of print_player.cpp#include "print_player.h"hand print_player(hand &myhand, apstring &player_info){	if (player_info == "money")	{		if (myhand.empty == 1)		{			cout << "XXXXX";		}		else		{			cout << myhand.money;		}	}	if (player_info == "name")	{		if (myhand.empty == 1)		{			cout << "XXXXXXXX";		}		else		{			cout << myhand.name;		}	}	if (player_info == "cards")	{		if ((myhand.empty == 1) || (myhand.folded == 1))		{			cout << "XxXx";		}		else		{			switch(myhand.big.suit)			{				case "0": cout << "c";						  break;				case "1": cout << "d";						  break;				case "2": cout << "h";						  break;				case "3": cout << "s";						  break;			}			switch(myhand.big.value)			{				case "0": cout << "2";						  break;				case "1": cout << "3";						  break;				case "2": cout << "4";						  break;				case "3": cout << "5";						  break;				case "4": cout << "6";						  break;				case "5": cout << "7";						  break;				case "6": cout << "8";						  break;				case "7": cout << "9";						  break;				case "8": cout << "T";						  break;				case "9": cout << "J";						  break;				case "10": cout << "Q";						   break;				case "11": cout << "K";						   break;				case "12": cout << "A";						   break;			}			switch(myhand.small.suit)			{				case "0": cout << "c";						  break;				case "1": cout << "d";						  break;				case "2": cout << "h";						  break;				case "3": cout << "s";						  break;			}			switch(myhand.small.value)			{				case "0": cout << "2";						  break;				case "1": cout << "3";						  break;				case "2": cout << "4";						  break;				case "3": cout << "5";						  break;				case "4": cout << "6";						  break;				case "5": cout << "7";						  break;				case "6": cout << "8";						  break;				case "7": cout << "9";						  break;				case "8": cout << "T";						  break;				case "9": cout << "J";						  break;				case "10": cout << "Q";						   break;				case "11": cout << "K";						   break;				case "12": cout << "A";						   break;			}		}	}	return myhand;}