// Library Implementation of round_3.cpp#include "round_3.h"table round_3(table &mytable){	apstring continue_play;	update_player(mytable);	int k, j;		print_table(mytable);	cout << "Type in anything and hit enter to continue play.";	cin >> continue_play;	cout << endl;	k = 1;		while ((((mytable.currentplayer != mytable.lastraiser) && (mytable.betsinround > 0)) ||			 (mytable.betsinround == 0) && (mytable.player[(mytable.lastraiser)].action == 'e')) &&			(k == 1))	{		j = 1;		if ((last_standing(mytable)) < 0)		{					if ((mytable.player[(mytable.currentplayer)].folded == 1) ||				(mytable.player[(mytable.currentplayer)].empty == 1))			{			}			else			{						if (mytable.currentplayer == mytable.human)				{					print_table(mytable);					cout << "Bet(b), Call or Check(c), or Fold(f): ";					cin >> mytable.player[(mytable.human)].action;					cout << endl;										if (mytable.player[(mytable.human)].action == 'b')					{						if (mytable.betsinround == 4)						{							cout << "This is the 4th round of betting and you can no longer bet. Choose again." << endl;							mytable.player[(mytable.currentplayer)].action = 'e';							j = 0;						}						else						{							get_action(mytable);							print_table(mytable);							cout << "Type in anything and hit enter to continue play.";							cin >> continue_play;							cout << endl;						}					}					if ((mytable.player[(mytable.human)].action == 'c') || 						(mytable.player[(mytable.human)].action == 'f'))					{						get_action(mytable);						print_table(mytable);						cout << "Type in anything and hit enter to continue play.";						cin >> continue_play;						cout << endl;					}					if ((mytable.player[(mytable.human)].action != 'b') &&						(mytable.player[(mytable.human)].action != 'c') &&						(mytable.player[(mytable.human)].action != 'f'))					{						cout << "That is not a legal function. Choose again." << endl;						mytable.player[(mytable.currentplayer)].action = 'e';						j = 0;					}				}				if (mytable.currentplayer != mytable.human)				{					bet_4th(mytable);							get_action(mytable);					print_table(mytable);					cout << "Type in anything and hit enter to continue play.";					cin >> continue_play;					cout << endl;				}			}			if (j == 1)			{				mytable.currentplayer = ((mytable.currentplayer + 1) % 10);			}		}		if ((last_standing(mytable)) >= 0)		{			mytable.player[(last_standing(mytable))].money =				(mytable.pot + mytable.player[(last_standing(mytable))].money);			k = 0;		}			}		if (k == 1)	{		int winner_is_you, winners[10], i;		float best_hand;				mytable.rounds = 4;		best_hand = 0;				for (i = 0; i < 10; i++)		{			winners[i] = 0;		}		for (i = 0; i < 10; i++)		{			if ((mytable.player[i].empty == 1) ||				(mytable.player[i].folded == 1))			{			}			else			{				hand_worth(mytable.player[i], mytable);				if (mytable.player[i].type > best_hand)				{					best_hand = mytable.player[i].type;					winners[i] = 1;					winner_is_you = i;										if (i == 0)					{					}					else					{						for (j = 0; j < i; j++)						{							winners[j] = 0;						}					}				}				if (mytable.player[i].type == best_hand)				{					winners[i] = 1;				}			}		}		j = 0;		for (i = 0; i < 10; i++)		{			j = (winners[i] + j);		}		for (i = 0; i < 10; i++)		{			if (winners[i] == 0)			{			}			else			{				mytable.player[i].money =					(mytable.player[i].money + (mytable.pot / float(j)));				int(mytable.player[i].money);			}		}							cout << "Player " << winner_is_you << " has won with a " << mytable.player[(winner_is_you)].type << "!" << endl;		print_table(mytable);					cout << "Type in anything to continue to next game.";		cin >> continue_play;		cout << endl;		loose_passive(mytable);		return mytable;	}	if (mytable.rounds == 3)	{		loose_passive(mytable);	}	return mytable;}